mapper.update:

UPDATE
	TBL_PLAN 
SET 
	P_ORI_NO = #{p_no}
WHERE 
	P_NO = #{p_no}

mapper.select:

SELECT 
	* 
FROM 
	TBL_PLAN 
WHERE 
	M_ID = #{m_id}
	AND P_YEAR = #{p_year}
	AND P_MONTH = #{p_month}

mapper.update:
UPDATE TBL_PLAN SET P_YEAR = #{p_year}, P_MONTH = #{p_month}, P_DATE = #{p_date}, P_TITLE = #{p_title}, P_BODY = #{p_body},
P_IMG_NAME = #{p_img_name},
P_MOD_DATE = NOW() WHERE P_NO = #{p_no}
